import BlogPreview from '../components/BlogPreview';
import useFetch from '../UseFetch';
const Home = () => {
  const {
    error,
    isPending,
    data: blogs
  } = useFetch('http://localhost:1337/posts');
  return (
    <div className='home'>
      {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>}
      {blogs && <Blogs blogs={blogs} />}
      <div className='top-rated grid grid-cols-1 gap-8 justify-items-center'>
        <h1 className='text-4xl my-12 '>Top Rated Blogs</h1>
        <BlogPreview />
        <BlogPreview />
        <BlogPreview />
      </div>
      <div className='top-rated grid grid-cols-1 gap-8 justify-items-center'>
        <h1 className='text-4xl my-12 '> Blogs About Health</h1>
        <BlogPreview />
        <BlogPreview />
        <BlogPreview />
      </div>
      <div className='top-rated grid grid-cols-1 gap-8 justify-items-center'>
        <h1 className='text-4xl my-12 '>Blogs About Wealth</h1>
        <BlogPreview />
        <BlogPreview />
        <BlogPreview />
      </div>
      <div className='top-rated grid grid-cols-1 gap-8 justify-items-center'>
        <h1 className='text-4xl my-12 '>Blogs About Wisdom</h1>
        <BlogPreview />
        <BlogPreview />
        <BlogPreview />
      </div>
    </div>
  );
};

export default Home;
